INSERT INTO `tours_health` (`id`, `catalog_id`, `title`, `text`, `author_id`, `editor_id`, `created_at`, `edited_at`, `publish_at`, `meta_keywords`, `meta_description`, `image`) VALUES
(5, 3, 'Новий рік 2014 у Львові.', '<h1 style="text-align: center;"><span style="color:#FF0000;"><strong>НОВИЙ РІК 2014 У ЛЬВОВІ</strong></span><br />\n<span style="color:#FF0000;"><strong>30.12.2013-01.01.2013 (3 дні/2 ночі)</strong></span></h1>\n\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:500px;">\n	<tbody>\n		<tr>\n			<td style="text-align: center;"><img alt="" src="/application/public/uploads/images/%D0%BD%20%D0%BB%201.jpeg" style="width: 389px; height: 280px;" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">1 день 30.12.13 понеділок</span></strong></span><br />\nПрибуття до Львова. Зустріч на вокзалі. Трансфер в готель. Поселення (згідно з правилами готелю)*.<br />\n<span style="color:#FF8C00;">Оглядова екскурсія по Львову (автобусна)</span>: захоплююча зустріч з минулим на горі Високий Замок, з якої видно увесь Львів - як на долоні, знайомство з княжим Львовом: придворною церквою Св. Миколая, церквою Св. Параскеви - у якій зберігся найдавніший львівський іконостас 17 ст., костелами св. Івана Хрестителя та Марії Сніжної, а далі: вишуканий сецесійний проспект Шевченка, архітектурний комплекс головного галицького греко-католицького храму - Собору Святого Юра, Університет, Львівська Політехніка, палац Потоцьких, Личаківський меморіальний цвинтар, занесений до світової спадщини ЮНЕСКО (могили Ів.Франка, М. Шашкевича, С. Крушельницької, композиторів С. Людкевича, А. Кос-Анатольського та В. Івасюка, архітектора З. Горголевського). Наприкінці екскурсії відвідання <span style="color:#FF8C00;">музею пива з дегустацієї львського пива</span> (додатково оплачується квиток 20грн за особу).<br />\nПропонуємо Вам (факультативно):<br />\n- <span style="color:#FF8C00;">Екскурсія &laquo;Нічна варта&raquo; </span>(факультативно 120грн за особу, потрібно бронювати при бронюванні туру). Орієнтовний початок екскурсії о 21.00 (точна година буде в інформаційному листі туриста, тривалість екскурсії 1,5-2 години):<br />\nЯкщо, випадково, вночі не спатиметься, визирніть у вікно &ndash; і побачите людей з ліхтарями. Не лякайтесь, це &ndash; помічники бургомістра&hellip; Вони просто бережуть Ваш сон&hellip;<br />\nНесподівані зустрічі, моторошні легенди, привиди, таємні обряди, загадкові вулиці та темні закутки &mdash; все це чекає на Вас під час нічної мандрівки Львовом!<br />\nЯкщо не боїтесь стати помічником пана Шлюсселя чи пані Зосі &mdash; приєднуйтесь!<br />\nЗа якісну роботу Вас посвятять в працівники Нічної варти Львова.</p>\n\n<p><span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">2 день 31.01.13 вівторок</span></strong></span><br />\nПішохідна екскурсія<span style="color:#FF8C00;"> &laquo;Місто з ароматом кави&raquo;:</span> Під час екскурсії Ви дізнаєтеся про історію вживання кави, її шляхи в Європу та до Львова, історію львівських кав&rsquo;ярень, оглянете топ-десятку найкращих львівських кав&rdquo;ярень. Розпочнемо подорож з площі Ринок &ndash; серця старовинного міста, пройдемся вузенькими середньовічними вуличками з величним ансамблем Успенської церкви - архітектурним шедевром українського ренесансу, бароковим Домініканським костелом &ndash; у якому можна послухати органну музику, єдиною у Львові готичною пам&rdquo;яткою - Латинською катедрою, яку будувати майже 100 років, каплицею Боїмів &ndash; усипальницею багатих міщан, вул. Вірменською з унікальною Вірменською церквою та будинком &bdquo;пір року&rdquo;, Королівським та міським Арсеналом, комплекс Бернардинського монастиря.... Ви почуєте безліч легенд про місто, його мешканців&cedil; прогуляєтеся центральним проспектом Львова &ndash; пр. Свободи з пам&rdquo;ятником Т. Шевченку та Оперним театром - архітектурною перлиною Львова, одним з найгарніших театрів Європи. Сфотографуєтеся з пам&rdquo;ятником бравому солдату Швейку, зможете скуштувати справжньої львівської кави у найдавнішій в місті &bdquo;Віденській кав&#39;ярні&rdquo; (за додаткову плату), яка в 1829 році була спроектована і збудована власне як кав&#39;ярня, а не пристосована, як інші. За свою 170 - річну історію кав&#39;ярня не втратила австрійської автентичної атмосфери, польського шарму та української гостинності.<br />\nПідготовка до святкування Нового 2014 року.<br />\n<span style="color:#FF0000;">СВЯТКУВАННЯ НОВОГО 2014 РОКУ!!! </span>(програми святкування Нового року будуть запропоновані відразу, як тільки їх нададуть готелі та ресторани)</p>\n\n<p><br />\n<span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">3 день 01.01.14 середа</span></strong></span><br />\nСніданок.<br />\n14.00 -17.00 Пішохідна екскурсія <span style="color:#FF8C00;">&laquo;Австрійський Львів&raquo;</span>: будинок вчених (Казино), Львівський Національний Університет, палац Потоцьких, проспект Т.Г. Шевченка, проспект Свободи, площа А. Міцкевича та ін. Під час екскурсії ви дізнаєтесь історію міста Львова в період XVIII &ndash; поч. XX ст.</p>\n\n<p><span style="color:#800080;"><strong>У вартість включено:</strong></span><br />\n- проживання (2 ночі) у готелі<br />\n- 2&nbsp;сніданки;<br />\n- трансфер вокзал-готель;<br />\n- оглядова автобусна екскурсія по Львову;<br />\n- пішохідна екскурсія &laquo;Місто з ароматом кави&raquo;,<br />\n- пішохідна екскурсія &laquo;Австрійський Львів&raquo;.<br />\nВиїзд зі Львова.</p>\n\n<h2 style="text-align: center;"><span style="color:#FF0000;"><strong>Ціна в грн. на 1 особу (мін.2 ос.):</strong></span></h2>\n\n<h2 style="text-align: center;"><span style="color:#FF0000;"><strong>Готель &laquo;Гетьман&raquo; (стандарт) &ndash; 900 грн</strong></span></h2>\n\n<p><span style="color:#800080;"><strong>Додатково оплачується:</strong></span><br />\n- вхідні квитки;<br />\n- факультативні програми &ndash; потрібно бронювати при бронюванні туру!<br />\n- Новорічна программа (не обов&rsquo;язкова). Програми святкування Нового року будуть запропоновані відразу, як тільки їх нададуть готелі та ресторани.</p>\n', 1, 1, 1380283338, 1380284314, 0, '', '', '7138028430795.jpeg'),
(6, 3, 'Новорічна програма 2014 в Олеському замку, Львів.', '<h1 style="text-align: center;"><strong><span style="color:#FF0000;">Новорічна програма 2014 в Гридниці Олеського замку</span></strong></h1>\n\n<table border="0" cellpadding="1" cellspacing="1" style="width:500px;">\n	<tbody>\n		<tr>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BE%D0%BB%D0%B5%D1%81%D1%8C%D0%BA%D0%B8%D0%B9%201.jpeg" style="width: 275px; height: 183px;" /></td>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BE%D0%BB%D0%B5%D1%81%D1%8C%D0%BA%D0%B8%D0%B9%202.jpeg" style="width: 259px; height: 194px;" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><br />\nМи всі були дітьми. Ми всі вірили (а може, й до сьогодні вірим?) в казку. І кожен з нас, в глибині душі, і зараз бажає потрапити до казки. В таємничу Новорічну ніч, в стінах древнього Олеського замку-мовчазного свідка спливаючих століть -Ви станете співучасником дива. Тіні минулого оживуть і разом з Вами очікуватимуть миті, коли сьогодні перетворюється на вчора, коли можна забажати будь-що, і воно обовязково стане реальністю, коли настає перша хвилина Нового прийдешнього року..</p>\n\n<table border="0" cellpadding="1" cellspacing="1" style="width:500px;">\n	<tbody>\n		<tr>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BE%D0%BB%D0%B5%D1%81%D1%8C%D0%BA%D0%B8%203.jpeg" style="width: 275px; height: 183px;" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>31 грудня о 19.30 у Гридниці починається справжня середньовічна казка &laquo;Легенди старого замку&raquo;. Вплетені в давню легенду лицарські бої за серце прекрасної дами , шляхетні танці під музику мандрівних менестрелів, забави та конкурси від ведучого-герольда, прогулянка на санчатах занурять Вас в атмосферу свята, гридницькі наїдки та напої, серед яких особливої уваги заслуговують мясо косулі та гаряче вино, наповнять тіло міццю й теплом, а видовищне вогняне шоу зачарує погляди та зігріє серця усіх присутніх.</p>\n\n<p>Приблизна (по часу) програма:<br />\n19.30 &ndash; приїзд гостей<br />\n19.30-20.00 &ndash; зустріч гостей перед замком<br />\n20.00-21.45 - перше застілля, дегустація наливок.<br />\n21.45-23.45 &ndash; концерт середньовічної музики та майстер &ndash; клас середньовічних танців<br />\n23.45-01.30 &ndash; проводжання Старого та зустріч Нового року на вулиці (вогні, забави старовинні, показові лицарські бої, глінтвейн при зорях, катання на санях по території замку).<br />\n01.30-02.30 &ndash; друге застілля, продовження програми в залі<br />\n02.30 &ndash; 03.30 - кава, чай, солодке, фотографії в середньовічних костюмах, танці.</p>\n\n<h2 style="text-align: center;"><span style="color:#FF0000;">Вартість програми 1600 грн. за 1 особу.<br />\nДітям від 7 до 14 р знижка 50%</span></h2>\n\n<p style="text-align: center;">&nbsp;</p>\n', 1, 1, 1380284683, 1384774424, 0, '', '', '84138028581534.jpeg'),
(7, 4, 'Новий рік у Кракові 4 дні', '<h1 style="text-align: center;"><span style="color:#FF0000;"><strong>Львів &ndash; Краків &ndash; Закопане &ndash; Львів</strong></span></h1>\n\n<p>&nbsp;</p>\n\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width: 128px;">\n	<tbody>\n		<tr>\n			<td style="width: 118px; text-align: center;"><span style="font-size:16px;">&nbsp;<span style="color:#800080;"><strong>грудень</strong></span>&nbsp;</span></td>\n		</tr>\n		<tr>\n			<td style="width: 118px; text-align: center;"><span style="font-size:16px;"><span style="color:#800080;">30</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:500px;">\n	<tbody>\n		<tr>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BD%20%D0%BA%201.jpeg" style="width: 214px; height: 120px;" /></td>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BD%20%D0%BA%203.jpg" style="height: 120px; width: 180px;" /></td>\n			<td><img alt="" src="/application/public/uploads/images/%D0%BD%20%D0%BA%204.jpg" style="height: 120px; width: 180px;" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><br />\n<span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">30.12.2013р.</span></strong></span><br />\nЛьвів. Зустріч кожного гостя представником фірми.<br />\n22:00 &ndash; Виїзд на кордон.</p>\n\n<p><br />\n<span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">31.12.2013р.</span></strong></span><br />\n9:00 &ndash; оглядова екскурсія <span style="color:#FF8C00;"><strong>&laquo;Королівський шлях&raquo;</strong></span>. Барбакан, Ринкова площа, собор Св. Діви Марії, Торгові ряди Сукєнніце, Ягеллонський університет &ndash; один з найстаріших у Європі, собор св. Петра і Павла, монастир Францисканців, Вавельський пагорб, і, звичайно ж, Кафедральний собор і печера вавельського Смока!!! &nbsp;Вільний час.<br />\n18:00 &ndash; повернення в готель. І головне - підготовка до зустрічі Нового року!<br />\n<span style="color:#A52A2A;"><strong>НОВОРІЧНА ЗАБАВА 2014 РОКУ!</strong></span></p>\n\n<p><br />\n<span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">01.01.2014р.</span></strong></span><br />\nСніданок. Вільний день.<br />\n10:00 &ndash; Рекомендуємо: екскурсія <span style="color:#FF8C00;"><strong>&laquo;Зимова казка Закопаного&raquo;</strong></span> (25&euro;). Велич гір &ndash; непостійних, гордих і безмежно красивих манить своєю неприступністю: Губалувка, Каспровий Верх &ndash; мрія кожного. Іде легкий сніг, грає музика, а Ви насолоджуєтесь смаженими ковбасками і милуєтесь чарівними видами. Повернення в готель. Ночівля.</p>\n\n<p><br />\n<span style="color:#0000CD;"><strong><span style="background-color:#00FFFF;">02.01.2014р.</span></strong></span><br />\nСніданок. Виселення з готелю.<br />\nРекомендуємо: екскурсія в <span style="color:#FF8C00;"><strong>Соляні копальні Велічка</strong></span> (25&euro; дорослі, 20&euro; діти до 12 років). Підземні лабіринти довжиною понад 300км, соляні озера, штреки та величезні комори, утворені внаслідок видобування солі, каплиця Св. Кінгі на глибині 101м &ndash; це варто побачити! В копальню варто захопити з собою теплі речі.&nbsp;<br />\nПереїзд в Україну. Прибуття у Львів (ввечері, до 23:00).</p>\n\n<p>Фірма залишає за собою право змінювати програму туру без зменшення загального обсягу послуг. Автобусне обслуговування у вільний час не передбачене. Мінімальна кількість туристів для здійснення факультативної програми &ndash; 30 осіб.</p>\n\n<p>Вартість факультативних програм складається з вартості вхідних квитків та/або транспортного обслуговування та/або послуг гіда та/або резервації. Вартість оплачених та невикористаних послуг не повертається!</p>\n\n<p>У програмах туру вказано орієнтовний час. Точне місце і час від&#39;їзду з міста, початок екскурсій і додаткової програми оголошується керівником групи.</p>\n\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:500px;">\n	<tbody>\n		<tr>\n			<td><span style="font-size:16px;"><span style="color:#0000CD;"><strong>Вартість</strong></span></span></td>\n			<td><span style="font-size:16px;"><span style="color:#0000CD;"><strong>Дорослі</strong></span></span></td>\n			<td><span style="font-size:16px;"><span style="color:#0000CD;"><strong>Діти до 16 років</strong></span></span></td>\n		</tr>\n		<tr>\n			<td><span style="color:#0000CD;"><strong>до 20.10</strong></span></td>\n			<td><span style="color:#000000;"><strong>145є</strong></span></td>\n			<td><span style="color:#000000;"><strong>130є</strong></span></td>\n		</tr>\n		<tr>\n			<td><span style="color:#0000CD;"><strong>до 10.11</strong></span></td>\n			<td><span style="color:#000000;"><strong>155є</strong></span></td>\n			<td><span style="color:#000000;"><strong>140є</strong></span></td>\n		</tr>\n		<tr>\n			<td><span style="color:#0000CD;"><strong>з 11.11</strong></span></td>\n			<td><span style="color:#000000;"><strong>165є</strong></span></td>\n			<td><span style="color:#000000;"><strong>150є</strong></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p><strong>Вартість туру визначається датою оплати, а не датою оформлення замовлення!<br />\nДоплата за одномісне розміщення &ndash; 20&euro; / ніч.</strong></p>\n\n<p><strong>Вартість Новорічної вечері 80&euro;!</strong></p>\n\n<p>Оплата здійснюється в гривнях за фіксованим курсом 11,2500 грн за 1 &euro;.</p>\n\n<p>УВАГА! Польські візи оформлятимуться через візовий центр.<br />\nВартість послуги візового центру становить 225грн за візу незалежно, чи сплачується консульський збір, чи ні.</p>\n\n<p>&nbsp;</p>\n\n<p>ШТРАФНІ САНКЦІЇ:<br />\n&bull; При відмові від туру в період від 16.11.2013 до 29.11.2013 штраф &ndash; 25% від вартості туру;<br />\n&bull; При відмові від туру в період від 30.11.2013 до 13.12.2013 штраф &ndash; 50% від вартості туру;<br />\n&bull; При відмові від туру від 14.12.2013 &ndash; 100% від вартості туру;<br />\n&bull; Консульский збір та послуги візового центру не повертаються після подачі документів у Консульство.</p>\n\n<p>&nbsp;</p>\n\n<p><strong>ЗНИЖКИ: Знижка 10&euro; для власників дійсної на час туру шенгенської візи при наявності у нас заповненої Вами розписки.</strong></p>\n\n<p>Подарунок від фірми:</p>\n\n<p>подарунок на згадку про Польщу.</p>\n\n<p><span style="font-size:16px;"><span style="color:#A52A2A;"><strong>У вартість туру входить: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></span></span>&nbsp;- зустріч представником фірми;<br />\n- проїзд комфортабельним автобусом єврокласу (кондиціонер, аудіо, відео);<br />\n- проживання в готелі***, сніданки шведський стіл;</p>\n\n<p>Туристи, що подорожують одні, можуть бути поселені у тримісний номер &ndash; це може бути двомісний номер з додатковим ліжком.<br />\nУ випадку відсутності підселення турист поселяється в одномісний номер.<br />\n- супровід керівника групи по всьому маршруту;<br />\n- екскурсії згідно програми.</p>\n\n<p><span style="color:#A52A2A;"><span style="font-size:16px;"><strong>У вартість туру не входить: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </strong></span></span>- вхідні квитки;<br />\n- індивідуальне медичне страхування на період туру &ndash; 21,52грн, страхова компанія Граве Україна, асистуюча компанія Mondial assistance, програма страхування &laquo;Standard&raquo;. Якщо вік туриста від 60 до 69 років, коефіцієнт страхування збільшується в 1,5 рази, якщо вік туриста від 70 до 74 років - 2 рази, від 75 до 79 років - 3 рази, від 80 до 84 років - 4 рази. Особи, старше 85 років на страхування не приймаються;<br />\n- факультативні програми;<br />\n- проїзд в громадському транспорті та таксі;<br />\n- новорічна забава 80&euro; (замовляти не обов&#39;язково), добирання до та з ресторану самостійно;<br />\n- консульський збір 35&euro; для громадян України;<br />\n- послуги візового центру 225грн.</p>\n\n<p>Важливо:</p>\n\n<p><strong>Автобус:</strong><br />\nЗ усіх питань, пов&rsquo;язаних з функціонуванням автобусу &ndash; температурний режим в салоні, функціонування сидінь, індивідуальних обдувів та освітлення, потрібно звертатись до керівника групи та не відволікати ними водіїв.<br />\nВ салоні автобусу категорично заборонено: ходити по салону під час руху автобусу, курити, лузати насіння, смітити, споживати алкогольні напої, морозиво, будь-які рибні, молочні продукти, варені яйця та інші продукти, які мають явно виражений специфічний запах.<br />\nВ салоні автобусу заборонено перевозити дорожні сумки, валізи та інший габаритний багаж. За порушення цього пункту можливий штраф в розмірі від 50&euro;.<br />\nВ салоні автобусу заборонено зберігати на верхніх поличках скляну тару та інші речі, які можуть травмувати пасажирів.<br />\nТуристи в стані алкогольного або наркотичного сп&rsquo;яніння до поїздки не допускатимуться.<br />\nЧай, кава в автобусі не готуються, їх можна придбати в кафе під час зупинок.<br />\nТуалет в автобусі відсутній (зачинений). Користування туалетом передбачене на санітарних зупинках &ndash; для цього закладіть, будь ласка, в бюджет подорожі 10-15&euro;.<br />\nЗупинки автобуса здійснюються у відповідності до програми туру у відведених для цього місцях. Всі місця зупинок обладнані туалетами. Зупинки на вимогу чи прохання туристів категорично заборонені.<br />\nСпинки сидінь в останньому ряді не опускаються.<br />\nТуристична фірма &laquo;Алголь&raquo; не несе відповідальності за залишені в салоні автобусу речі, кошти та документи.<br />\nТуристи несуть повну матеріальну відповідальність за псування майна автобусу.</p>\n\n<p><strong>Готель:</strong><br />\nПоселення в готель після 14:00, виселення з готелю до 10:00.<br />\nПісля 22:00 слід дотримуватись тиші.<br />\nПродукти на сніданку призначені для споживання в приміщенні ресторану. За спробу винести продукти харчування з ресторану адміністрацією готелю може бути накладено штраф або викликано поліцію.<br />\nДля збереження цінних речей, коштів та документів радимо користуватись сейфом готелю.<br />\nВ числі телевізійних каналів можуть бути платні. Оплата таких каналів здійснюється по факту їхнього користування.<br />\nВміст мінібарів в номерах за додаткову плату. При поселенні вміст мінібару потрібно звірити з описом. Недостача вмісту мінібару при виселенні прирівнюється до споживання та підлягає оплаті.<br />\nХолодильника, праски та фену в номері немає.<br />\nТуристи несуть повну матеріальну відповідальність за псування майна готелю.</p>\n\n<p><strong>Страхування:</strong><br />\nПросимо до початку туру ознайомитись з правилами страхування та умовами відшкодування медичних витрат страховою компанією.<br />\nЯкщо під час туру передбачаються зайняття спортом або розваги, пов&#39;язані з підвищеною небезпекою для здоров&#39;я, життя, потрібно вибрати відповідну програму страхування.<br />\nЗвертаємо увагу, що страхова компанія не відшкодовує витрати, пов&#39;язані з лікуванням травм, станів та захворювань, які застрахована особа отримала внаслідок дій, здійснених нею у стані алкогольного сп&#39;яніння або під впливом наркотичних чи токсичних речовин.</p>\n\n<p><strong>Керівник групи:</strong><br />\nСупроводжує групу по всьому маршруту туру, забезпечує повне виконання програми туру, інформує про час та місце збору та/або від&rsquo;їзду групи, організовує посадку в автобус та поселення в готелях відповідно до &laquo;Підтвердження замовлення&raquo; (&laquo;Інформаційного листа&raquo;).<br />\nУ випадках втрати туристами документів або страхових випадках допомагає туристам у взаємодії з консульськими службами України, місцевими правоохоронними органами, медичними закладами, страховими компаніями та перекладачами коштом туриста.<br />\nКерівнику групи категорично заборонено змінювати програму туру на вимогу чи прохання туристів.</p>\n\n<p><strong>Попередження</strong>:<br />\nТурист, що виїжджає з автобусним туром, зобов&#39;язаний в&#39;їхати і виїхати з країн Європи в складі групи. У разі самовільної затримки туриста в країнах Європи відомості про даний інцидент негайно передаються в поліцію, імміграційну службу і консульство держави, що видала візу.<br />\nФірма не несе відповідальності за роботу консульських, митних, прикордонних, дорожніх служб, корки на дорогах, погодні умови<br />\nта пов&#39;язані з цим затримки в турі та можливі незручності.</p>\n\n<p><br />\n<span style="color:#FF0000;"><strong>НЕОБХІДНІ ДОКУМЕНТИ</strong></span></p>\n\n<p>Повний комплект документів на візу потрібно подати протягом 5 календарних днів після Замовлення туру, але не пізніше, ніж 5 грудня, та не раніше, ніж за 2 місяці до початку туру!</p>\n\n<p>З офіційною інформацією можна ознайомитись на веб-сторінці Генерального консульства Республіки Польща в м. Львові.</p>\n\n<p>Всі довідки, витяги, виписки повинні бути видані не давніше 3 місяців до початку туру!</p>\n\n<p>У випадку ненадання хоча б одного з необхідних документів, або надання у невідповідності до вимог, &ndash; документи в роботу не приймаємо!</p>\n\n<p><br />\n<strong>Загальні документи:</strong></p>\n\n<ol>\n	<li>&nbsp;Анкета туриста згідно зразка&nbsp;</li>\n	<li>Анкета для консульства Польщі</li>\n	<li>Дійсний закордонний паспорт (інший документ на право виїзду з України), <strong>а для дітей, яким виповнилось 14 років, </strong>проїзний документ: без наявних слідів пошкодження (тобто без порушення плівки на першій сторінці, без плям, не подерті тощо) з терміном дії не менше 3 місяців після дати закінчення туру наявними в ньому 2 чистими сторінками &ndash; для візи та штампу. Продовжені закордонні паспорти (інші документи на право виїзду з України) не приймаються! У випадку зміни прізвища не пізніше одного місяця потрібно виробити новий закордонний паспорт! У випадку наявності інших закордонних паспортів слід додати їх до пакету документів. <strong>Якщо дитині не виповнилося 5 років,</strong> дані про неї повинні бути вписані у закордонний паспорт (інший документ на право виїзду з України) особи, з якою вона подорожуватиме, а також в ньому мають бути ще 2 чисті сторінки &ndash; для візи цієї дитини та штампу. Якщо дитині виповнилось 5 років, вона не має окремого проїзного документу і є вписаною у паспорт особи, з якою вона подорожуватиме, фотографія цієї дитини повинна бути вклеєна в закордонний паспорт (інший документ на право виїзду з України) такої особи, а також в ньому мають бути ще 2 чисті сторінки &ndash; для візи цієї дитини та штампу.</li>\n	<li>Копія всіх сторінок (з відмітками і без) громадянського (внутрішнього) паспорта. Якщо в паспорті є непогашений штамп з номером другого закордонного паспорту, такий закордонний паспорт обов&#39;язково слід надати в консульство! Якщо на копії погано видно реєстрацію за місцем проживання, слід надати оригінал довідки з ЖЕКу про реєстрацію за місцем проживання! &laquo; Згорнути</li>\n	<li>Копія свідоцтва про одруження/розлучення/смерть одного з подружжя (якщо таке є).</li>\n	<li>Кольорова фотографія (Оригінал) &ndash; 2 шт. згідно зразка 3,5 Х 4,5 на білому фоні, 80% обличчя. Фотографія повинна бути не давніша за 3 місяці до початку туру!</li>\n	<li>Страховий поліс медичного страхування (Оригінал) &ndash; 1 шт. від страхової компанії, занесеної до списку акредитованих страхових компаній дійсна на території всіх шенгенських країн покриває матеріальні витрати у розмірі 30000 євро на випадок надання швидкої медичної допомоги та якщо потерпілого від нещасного випадку потрібно довезти додому заповнена машинним друком дані туриста &ndash; прізвище, ім&#39;я &ndash; заповнені латинськими літерами згідно закордонного паспорта містить серію та номер закордонного паспорта містить адресу прописки та номер телефону ліміт перебування за кордоном не менше тривалості туру термін страхування покриває термін тривалості всього туру та закінчується не раніше, ніж через 15 днів після дати закінчення туру франшиза не повинна перевищувати 150 євро Один корінець страхового полісу подається в консульство і не повертається Квитанція про оплату страхового полісу (Оригінал) &ndash; 1 шт.</li>\n</ol>\n\n<p><strong>Для дітей до 18 років:</strong></p>\n\n<ol>\n	<li>У випадку виїзду з одним із батьків, або з іншою особою потрібно оригінал нотаріально завіреного дозволу на виїзд дитини за кордон згідно зразка, який залишається в консульстві. Таким оригіналом може бути нотаріально завірена &quot;фотокопія&quot; оригіналу дозволу, котра видається нотаріусом на прохання разом з оригіналом і плата за яку становить 5-10грн. Сам оригінал дозволу &ndash; для дітей до 16 років &ndash; потрібно мати з собою для пред&rsquo;явлення на українському кордоні &ndash; прохання його нам не передавати. У випадку передачі нам такого оригіналу вважатимемо, що ще один оригінал є у туристів для пред&rsquo;явлення на українському кордоні! У дозволі на виїзд повинно бути зазначено, що:дозвіл видається на період з дати початку туру і закінчується не раніше, ніж через 15 днів після закінчення терміну туру (радимо зазначати місячний період після закінчення терміну туру) дозвіл видається для відвідування Польщі, Словаччини, Угорщини, Чехії та інших країн-учасниць Шенгенської Угоди.</li>\n	<li>Свідоцтво про народження (Копія) &ndash; 1 шт.</li>\n	<li>Копія всіх сторінок громадянського (внутрішнього) паспорта (за наявності).</li>\n	<li>Довідка з місця навчання (Оригінал) &ndash; 1 шт.</li>\n	<li>Копії всіх сторінок (з відмітками і без) громадянського (внутрішнього) паспорта батька або Витяг з державного реєстру (ст.135) про батьківство зі слів матері (Оригінал) &ndash; 1 шт. або Внутрішній паспорт опікуна-батька (Копія) &ndash; 1 шт. або Рішення суду про позбавлення батьківських прав батька (Копія) &ndash; 1 шт. абоСвідоцтво про позбавлення батьківських прав батька (Копія) &ndash; 1 шт. або Свідоцтво про смерть батька (Копія) &ndash; 1 шт. Копії всіх сторінок (з відмітками і без) громадянського (внутрішнього) паспорта матері або &nbsp;Внутрішній паспорт опікуна-матері (Копія) &ndash; 1 шт. або Рішення суду про позбавлення батьківських прав матері (Копія) &ndash; 1 шт. або Свідоцтво про позбавлення батьківських прав матері (Копія) &ndash; 1 шт. або&nbsp;про смерть матері (Копія) &ndash; 1 шт.</li>\n</ol>\n\n<p><strong>Для пенсіонерів: </strong></p>\n\n<ol>\n	<li>Пенсійне посвідчення (Оригінал) &ndash; 1 шт.</li>\n	<li>Пенсійне посвідчення (Копія) &ndash; 1 шт.</li>\n</ol>\n\n<p><strong>Для студентів до 21 року:</strong></p>\n\n<ol>\n	<li>Довідка студента з місця навчання (Оригінал) &ndash; 1 шт.</li>\n	<li>Копія всіх сторінок (з відмітками і без) громадянського (внутрішнього) паспорта особи, що фінансує.</li>\n	<li>Документ, що підтверджує родинні зв&#39;язки з особою, що фінансує (Копія) &ndash; 1 шт.</li>\n	<li>Заява про фінансування від особи, що фінансує (Оригінал, можна від руки) &ndash; 1 шт.</li>\n	<li>Свідоцтво про народження (Копія) &ndash; 1 шт.</li>\n	<li>Студентський квиток (Оригінал) &ndash; 1 шт. або</li>\n	<li>Студентський квиток (Копія, нотаріально посвідчена) &ndash; 1 шт.</li>\n</ol>\n\n<p>&nbsp;</p>\n\n<p><strong>Для осіб, віком від 18 до 21 року, які ніде не навчаються і не працюють: </strong></p>\n\n<ol>\n	<li>Копія всіх сторінок (з відмітками і без) громадянського (внутрішнього) паспорта особи, що фінансує.</li>\n	<li>Документ, що підтверджує родинні зв&#39;язки з особою, що фінансує (Копія) &ndash; 1 шт.</li>\n	<li>Заява про фінансування витрат від особи, що фінансує (Оригінал, нотаріально посвідчений) &ndash; 1 шт.</li>\n	<li>Свідоцтво про народження (Копія) &ndash; 1 шт.</li>\n</ol>\n\n<p><br />\n&nbsp;&nbsp;</p>\n\n<p><strong>Документи приймаються з консульським збором.<br />\nКонсульський збір становить 35 євро!<br />\nКОНСУЛЬСЬКИЙ ЗБІР НЕ ПОВЕРТАЄТЬСЯ!</strong></p>\n\n<p><br />\n<u><strong>Консульський збір не сплачують:</strong></u><br />\nдіти, котрим на дату початку туру не виповнилось 18 років<br />\nучні та студенти, котрим на дату початку туру не виповнилося 21 року, за умови надання оригіналу довідки з місця навчання із зазначенням денної форми навчання (стаціонару) та копії студентського квитка<br />\nпенсіонери за умови надання оригіналу пенсійного посвідчення (немає різниці, чи пенсіонер за віком, чи за вислугою років)<br />\nінваліди за умови надання оригіналу посвідчення інваліда.</p>\n\n<p><br />\nПрохання разом з документами туристів надавати нам Підтвердження замовлення.<br />\nПрохання залишати в себе копії всіх документів, які Ви нам надаєте.</p>\n', 1, 1, 1380460083, 1380464603, 0, 'новий рік у кракові, польща від туристичної фірми резидент івано-франківськ, новорічний тур до польщі сього 145 є від туристичного агенства резидент івано-франківськ, незабутні новорчні тури до польщі, кракова від турфірми резидент івано-франківськ. найдешевші новорічні тури в польщі. акційні новорічні тури до польщі.', 'новий рік у кракові, польща від туристичної фірми резидент івано-франківськ, новорічний тур до польщі сього 145 є від туристичного агенства резидент івано-франківськ, незабутні новорчні тури до польщі, кракова від турфірми резидент івано-франківськ. найдешевші новорічні тури в польщі. акційні новорічні тури до польщі.', '');
